<?php //Función que nos retornara una conexión con mysqli    function crearConexion(){        //Datos para la conexión con el servidor        $servidor   = "localhost";        $nombreBD   = "slxcontrol" /*"solcloud_syscontrol"*/;        $usuario    = "root" /*"solcloud_cloud"*/;        $contrasena = /*"my_local"*/"solarlex" /*".tN}!*@4Ti;J"*/;        //Creando la conexión, nuevo objeto mysqli        $conexion = new mysqli($servidor,$usuario,$contrasena,$nombreBD);        //Si sucede algún error la función muere e imprimir el error       /* verificar la conexión */         if (mysqli_connect_errno()) {             printf("Conexión fallida: %s\n", mysqli_connect_error());             exit();         }        //Si nada sucede retornamos la conexión        return $conexion;    }?>