<?php //Función que nos retornara una conexión con mysqli    function crearConexion(){        $path_xml = trim(shell_exec("find /var/www/html/slxcontrol -name configuracion.xml"));        $xml      = simplexml_load_file($path_xml);        $servidor  = $xml->mysql->host;        $nombre_bd = $xml->mysql->esquema;        $usuario   = $xml->mysql->usuario;        $password  = $xml->mysql->password;        //Datos para la conexión con el servidor        $servidor   = $servidor;        $nombreBD   = $nombre_bd;        $usuario    = $usuario;        $contrasena = $password;        //Creando la conexión, nuevo objeto mysqli        $conexion = new mysqli($servidor,$usuario,$contrasena,$nombreBD);        //Si sucede algún error la función muere e imprimir el error       /* verificar la conexión */         if (mysqli_connect_errno()) {             printf("Conexión fallida: %s\n", mysqli_connect_error());             exit();         }        //Si nada sucede retornamos la conexión        return $conexion;    }?>